$(document).ready(function(){if($(window).width()<850){$('ul .drop').css('display','none');}else{$('ul .drop').css('display','block')}$('ul .toggle').click(function(){$('ul .drop').toggle()});$('img.button').bind('mouseenter mouseleave',function(){$(this).attr({src:$(this).attr('hvr'),'hvr':$(this).attr('src')})});$(function(){var lastScroll=0;$(window).scroll(function(){var st=$(this).scrollTop();if(st>lastScroll){$('#up').css('display','none')}else{$('#up').css('display','block')}lastScroll=st})})});